def encode(text):
    """
    加密
    :param text: 源文本
    """
    encode_content = []
    for i in text:
        encode_content.append(format(ord(i), 'b'))
    print(" ".join(encode_content))


def decode(text):
    """
    解密
    :param text: 二进制文本
    :return:
    """
    content = text.split(" ")
    decode_content = []
    for i in content:
        decode_content.append(chr(int(i, 2)))
    print("".join(decode_content))


